import { useState } from "react";
import { Switch, Route, NavLink } from "react-router-dom";
import Movie from "./components/Movie";
import FavMovie from "./components/FavMovie";
import { useDispatch, useSelector } from "react-redux";
import { addToWatchlist } from "./store/action";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


function App() {
  const [sira, setSira] = useState(0);
  const favMovies = [];

  function sonrakiFilm() {
    setSira(sira + 1);
  };

    const dispatch = useDispatch();
    const watchlist = useSelector((state)=>state.watchlist);

    const handleAddtToWatchlist = (movie) => {
      dispatch(addToWatchlist(movie));
      toast.dark('Film listenize eklendi', {
        style: {
         background: '#1D4ED8',
        }
    });
    };

  return (
    <div className="wrapper max-w-2xl mx-auto">
      <nav className="flex text-2xl pb-6 pt-8 gap-2 justify-center">
        <NavLink to="/" exact className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Filmler
        </NavLink>
        <NavLink to="/listem" className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Listem
        </NavLink>
      </nav>
      <Switch>
        <Route exact path="/">
          <Movie sira={sira} />

          <div className="flex gap-3 justify-end py-3">
            <button
              onClick={sonrakiFilm}
              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500"
            >
              SÄ±radaki
            </button>
            <button onClick={()=>handleAddtToWatchlist()} className="select-none px-4 py-2 bg-blue-700 hover:bg-blue-600 text-white">
              Listeme ekle
            </button>
            <ToastContainer />
          </div>
        </Route>

        <Route path="/listem">
          <div>
            {favMovies.map((movie) => (
              <FavMovie key={movie.id} title={movie.title} id={movie.id} watchlist={watchlist}/>
            ))}
          </div>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
