import React from "react";
import { useDispatch } from "react-redux";
import { removeFromWatchlist } from "../store/action";

const FavMovie = ({title,id,watchlist}) => {
  const dispatch = useDispatch();

  const handleRemoveFromWatchlist = (id) =>{
    dispatch(removeFromWatchlist(id));
  };
  const isAlreadyInWatchlist = watchlist.some((movie)=> movie.id === id);
  return (
    <div className="flex p-3 pl-4 bg-white mb-2 shadow items-center group">
      <div className="pr-4 flex-1">{title}</div>
      <button onClick= {()=>handleRemoveFromWatchlist}className="px-3 py-2 block text-sm rounded bg-rose-700 text-white opacity-30 group-hover:opacity-100">
        {isAlreadyInWatchlist ? "Çıkar" : " "}
      </button>
    </div>
  );
};
export default FavMovie;
